import { useContext } from "react";
import styles from "../../components/menu.css.modules/AddToCartButton.module.css";
import { AddedArrayContext } from "../context/AddedArrayContext";
import { HeaderContext } from "../context/HeaderContext";

export const AddButton = ({ name, price }) => {
  const { setItemsArray } = useContext(AddedArrayContext);
  const { setIncrementCounter } = useContext(HeaderContext);

  const addToCart = () => {
    // Добавляем товар в массив корзины
    setItemsArray((prevItems) => {
      const existingItem = prevItems.find((item) => item.name === name);

      if (existingItem) {
        // Если товар уже есть, увеличиваем его количество
        return prevItems.map((item) =>
          item.name === name
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      } else {
        // Если товара ещё нет, добавляем новый
        return [...prevItems, { name, price, quantity: 1 }];
      }
    });

    // Увеличиваем общий счётчик товаров
    setIncrementCounter((prevCount) => prevCount + 1);
  };

  return (
    <button onClick={addToCart} className={styles.addToCartButton}>
      <span>Add to cart</span>
    </button>
  );
};
