import React, { createContext, useState } from "react";

export const AddedArrayContext = createContext();

export const AddedArrayProvider = ({ children }) => {
  const [itemsArray, setItemsArray] = useState([]);

  // Добавление товара в корзину
  const addItem = (item) => {
    setItemsArray((prevItems) => {
      const existingItem = prevItems.find((p) => p.id === item.id);
      if (existingItem) {
        // Если товар уже в корзине, увеличиваем количество
        return prevItems.map((p) =>
          p.id === item.id ? { ...p, quantity: p.quantity + 1 } : p
        );
      } else {
        // Если товара ещё нет, добавляем его
        return [...prevItems, { ...item, quantity: 1 }];
      }
    });
  };

  // Уменьшение количества товара
  const removeItem = (itemId) => {
    setItemsArray((prevItems) =>
      prevItems
        .map((p) =>
          p.id === itemId ? { ...p, quantity: p.quantity - 1 } : p
        )
        .filter((p) => p.quantity > 0) // Убираем товары с quantity = 0
    );
  };

  // Удаление товара полностью
  const deleteItem = (itemId) => {
    setItemsArray((prevItems) => prevItems.filter((p) => p.id !== itemId));
  };

  // Очистка всей корзины
  const clearCart = () => {
    setItemsArray([]);
  };

  return (
    <AddedArrayContext.Provider
      value={{ itemsArray, addItem, removeItem, deleteItem, clearCart }}
    >
      {children}
    </AddedArrayContext.Provider>
  );
};
