import { useContext } from "react";
import styles from "../../components/menu.css.modules/AddToCartButton.module.css";
import { AddedArrayContext } from "../context/AddedArrayContext";

export const AddButton = ({ pizza }) => {
  const { addItem } = useContext(AddedArrayContext);

  return (
    <button onClick={() => addItem(pizza)} className={styles.addToCartButton}>
      <span>Add to cart</span>
    </button>
  );
};
