import React from "react";
import { Outlet } from "react-router-dom";
import { HeaderContext } from "../components/Header";  // Подключаем HeaderContext
import { Header } from "../components/Header";
import Footer from "../components/Footer";

// Root будет обернут в HeaderContext.Provider, чтобы контекст был доступен везде
export const Root = () => {
  const [cartDisplay, setCartDisplay] = React.useState(false);

  const addButton = () => {
    setCartDisplay(true);  // Показываем корзину
  };

  return (
    // Оборачиваем весь Root в HeaderContext.Provider
    <HeaderContext.Provider value={{ addButton }}>
      <div>
        <Header /> {/* Header будет использовать контекст */}
        <Outlet />  {/* Это пространство для дочерних маршрутов */}
        <Footer />
      </div>
    </HeaderContext.Provider>
  );
};
