import React, { useState, createContext } from "react";
import { Link } from "react-router-dom";
import styles from "./Headers.module.css";

// Создаем контекст для Header
export const HeaderContext = createContext();

export const Header = () => {
  // Стейт для отображения и скрытия корзины
  const [cartDisplay, setCartDisplay] = useState(false);

  // Функция, которая будет вызываться для добавления в корзину
  const addButton = () => {
    setCartDisplay(true);  // Показываем иконку корзины
  };

  return (
    // Передаем addButton в value контекста
    <HeaderContext.Provider value={{ addButton }}>
      <header className={styles.header}>
        <nav>
          <ul className={styles.header_ul}>
            <Link to="/">
              <img className={styles.logo_image} src='../icons/AntonioLogo.svg' />
            </Link>
            <li><Link className={styles.header_links} to="/">Home</Link></li>
            <li><Link className={styles.header_links} to="/pizza">Pizza</Link></li>
            <li><Link className={styles.header_links} to="/pasta">Pasta</Link></li>
            <li><Link className={styles.header_links} to="/drinks">Drinks</Link></li>
            <li><Link className={styles.header_links} to="/deserts">Deserts</Link></li>
            {cartDisplay && <li><img className={styles.cart_icon} src='../icons/cart.svg' /></li>}
          </ul>
        </nav>
      </header>
      <h1 className="antonio_Head_Title">Uncle Antonio</h1>
    </HeaderContext.Provider>
  );
};
