import styles from "../components/Main.module.css";
import { Scroller } from "../components/Scroller";
export const Home = () => {
const scrollActivator = Scroller()
    return (
        <main>
            <div className={styles.woodBack}>
                    <h2>We are making real Italian pizza since 2024</h2>
                    <div className={styles.texts_Wrapper}>
                    <h3 className={scrollActivator ? "visible" : "notVisible"}>Our pizza is more than just a meal â€” it's an experience. From the hand-stretched dough to the house-made tomato sauce, every ingredient is chosen with care. We use only fresh, locally sourced produce and premium cheeses for a flavor that speaks for itself. Savor the difference in every bite!</h3>
                <div className={styles.pizzaWrapper}>
                        <div className={styles.rollingPizza}></div>
                </div>
                <h3 className={scrollActivator ? "visible" : ""}>Inspired by classic Italian recipes and perfected by our chefs, we bring you pizza that balances tradition with a modern twist. Using high-quality flour and a unique blend of spices, we achieve a perfect, crispy crust and bold, unforgettable taste. Discover your new favorite today! </h3>
                        </div>
            </div>
            <div className={styles.woodOven_Block}>
                <h2>We cook pizza in a wood-fired oven</h2>    
            </div>
        </main>
    )
}
