import {useContext} from 'react'
import styles from "../../components/menu.css.modules/CartButton.module.css";
import { HeaderContext } from "../context/HeaderContext";
import { CartSideMenu } from './CartSideMenu';
export const CartButton = () => {
    const {incrementCounter} = useContext(HeaderContext);
    return (
        <li > 
            <button on className={styles.cart_button}>
                <img className={styles.cart_icon} src='../icons/cart_oscure.svg' />
                <span className={styles.goldenCircle}>{incrementCounter}</span>
            </button>
        </li>
    )
}
