import { useContext } from "react";
import styles from "../../components/menu.css.modules/CartSide.module.css";
import { AddedArrayContext } from "../context/AddedArrayContext";

export const CartSideMenu = () => {
  const { itemsArray, addItem, removeItem, deleteItem, clearCart } =
    useContext(AddedArrayContext);

  return (
    <div className={styles.sideWrapper}>
      <h3>Your Cart</h3>
      {itemsArray.length > 0 ? (
        <ul>
          {itemsArray.map((item) => (
            <li key={item.id} className={styles.cartItem}>
              <p>{item.pizzaTitle}</p>
              <p>Price: {item.price}â‚¬</p>
              <p>Quantity: {item.quantity}</p>
              <div>
                <button onClick={() => addItem(item)}>+</button>
                <button onClick={() => removeItem(item.id)}>-</button>
                <button onClick={() => deleteItem(item.id)}>Remove</button>
              </div>
            </li>
          ))}
        </ul>
      ) : (
        <p>Your cart is empty.</p>
      )}
      <button onClick={clearCart}>Clear Cart</button>
    </div>
  );
};
